import {React, useState } from "react";
import logo from "./assets/investment-calculator-logo.png";

function CalculateForm() {

    const [yearlyData, setYearlyData] = useState([]); // State to store the yearly data

    const resetHandler = () => {
        // Reset the form fields and clear the yearly data
        setFormValues({
            currentSavings: "",
            yearlyContribution: "",
            expectedReturn: "",
            duration: "",
        });
        setYearlyData([]);
    };

    const calculateHandler = (event) => {
        event.preventDefault(); // Prevent the form from submitting and refreshing the page

        // Retrieve user input values
        const currentSavings = parseFloat(document.getElementById('current-savings').value);
        const yearlyContribution = parseFloat(document.getElementById('yearly-contribution').value);
        const expectedReturn = parseFloat(document.getElementById('expected-return').value) / 100;
        const duration = parseFloat(document.getElementById('duration').value);

        const calculatedYearlyData = []; // Temporary array to hold yearly data

        // Calculate yearly results
        let currentYearlySavings = currentSavings;

        for (let i = 0; i < duration; i++) {
            const yearlyInterest = currentYearlySavings * expectedReturn;
            currentYearlySavings += yearlyInterest + yearlyContribution;

            calculatedYearlyData.push({
                year: i + 1,
                yearlyInterest: yearlyInterest.toFixed(2), // Format to two decimal places
                savingsEndOfYear: currentYearlySavings.toFixed(2), // Format to two decimal places
                yearlyContribution: yearlyContribution.toFixed(2), // Format to two decimal places
            });
        }

        // Update the state with the calculated data
        setYearlyData(calculatedYearlyData);
    };

    return (
        <div>
            <header className="header">
                <img src={logo} alt="logo" />
                <h1>Investment Calculator</h1>
            </header>

            <form className="form" onSubmit={calculateHandler}>
                <div className="input-group">
                    <p>
                        <label htmlFor="current-savings">Current Savings ($)</label>
                        <input type="number" id="current-savings" />
                    </p>
                    <p>
                        <label htmlFor="yearly-contribution">Yearly Savings ($)</label>
                        <input type="number" id="yearly-contribution" />
                    </p>
                </div>
                <div className="input-group">
                    <p>
                        <label htmlFor="expected-return">
                            Expected Interest (%, per year)
                        </label>
                        <input type="number" id="expected-return" />
                    </p>
                    <p>
                        <label htmlFor="duration">Investment Duration (years)</label>
                        <input type="number" id="duration" />
                    </p>
                </div>
                <p className="actions">
                    <button type="reset" className="buttonAlt" onSubmit={restHandler}>
                        Reset
                    </button>
                    <button type="submit" className="button" >
                        Calculate
                    </button>
                </p>
            </form>

        </div>
    );

            }

export default CalculateForm;
